mapper.select:
SELECT COUNT(*)
FROM user
WHERE user_id = #{userId}

mapper.update:
UPDATE `user`
SET
    user_name = #{userName},
    email = #{email},
    tel = #{tel}
WHERE user_id = #{userId}

UPDATE user
SET total_point = total_point + #{amount}
WHERE user_pk = #{userPk}

UPDATE `user`
SET email = #{email},
    tel = #{tel},
    access_token = #{accessToken},
    refresh_token = #{refreshToken}
WHERE user_pk = #{userPk}
